        position.set(Position.KEY_IGNITION, buf.readUnsignedByte() > 0);
        position.set(Position.KEY_POWER, buf.readUnsignedShort() * 0.01);
        position.set(Position.KEY_BATTERY, buf.readUnsignedShort() * 0.01);

